class _HomeScreenState extends State<HomeScreen> {

  Future <void> getNotified() async {
    NotificationsList=[];
    AppointmentsList=[];
    // Create a reference to the users collection
    final usersRef = FirebaseFirestore
        .instance
        .collection('Users')
        .doc(FirebaseAuth.instance.currentUser!.uid)
        .collection('Appointment');

    // Get all the documents from the collection
    final documents = await usersRef.get();
    // Loop through each document and get its data
    for (final document in documents.docs) {
      final data = document.data();
      AppointmentsList.add(data);
      NotificationsList.add(data);
      // print(data);
      // print('Notification list is  $NotificationsList');
      // print('Appointments list is  $AppointmentsList');
    }
  }
